storecustom.scss:
// @import url('http://fonts.cdnfonts.com/css/dubai');
@import url('https://fonts.googleapis.com/css2?family=Open+Sans&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Almarai&display=swap');

html{
    scroll-behavior: smooth;
}
.spacing-bottom{
    padding-bottom:100px;
    background-color:white;
    color:white;
}
.top-0{top:0;}.bottom-0{bottom:0;}.left-0{left:0;}.right-0{right:0;}
.h2-banner-title{
    font-family: 'Almarai';
    font-weight: 500;
    letter-spacing: 2px;
}

/* Header */
    .home-body{
         padding-top: 0 !important;
    }


    // header.header{
    //     background: transparent !important;
    //     border-bottom: 0;
    // }
    .header.slim {
        background: #fff !important;
        background-position: top;
        background-repeat: no-repeat;
        background-size: cover !important;
        width: 100%;
        border-bottom: 1px solid #e0e0e0 !important;
        @media screen and (min-width: 993px){
            height: 93px !important;
        }
        @media only screen and (min-width: 801px) and (max-width: 992px){
            height: 58px !important;
        }
        @media screen and (max-width: 800px){
            height: 58px !important;
        }

        .navPages-action{
            color: #222222 !important;
            font-weight: 600 !important;   
            &:hover{
                text-decoration: 3px solid #8bd8bd underline !important;
            }   
        }
        .navUser-action{
            color: #222222;
            font-weight: 600;
            &:hover{
                text-decoration: none !important;
            }
        }
        .h-hide{
            display: none;
        }
        .h-show{
            display: block;
        }
        
        .navPages-action svg{
            fill: #222222 !important;
            stroke: #222222 !important;
        }
        .mobileMenu-toggle .mobileMenu-toggleIcon, .mobileMenu-toggle .mobileMenu-toggleIcon::before, .mobileMenu-toggle .mobileMenu-toggleIcon::after, .mobileMenu-toggle.is-open .mobileMenu-toggleIcon::before, .mobileMenu-toggle.is-open .mobileMenu-toggleIcon::after {
            background: #222222 !important;
        }
        .header-logo-image-container {
            height: 50px;
            width: auto;
        }
    }
// }

/*Header Toggle*/
    .navPages {
        @media screen and (max-width: 992px){
            width: calc(100% - 75px) !important;
            margin-left: 0 !important;
        }
    }

    .navPages-container.is-open {

        .navPages{
            @media screen and (max-width: 992px){
                padding: 1.5rem;
            }
        }

        .mobile-closeNav {
            @media screen and (max-width: 992px){
                position: absolute;
                right: 20px;
                top: 20px;
                font-size: 0;
                cursor: pointer;
                display: block;
            }
            .icon {
                @media screen and (max-width: 992px){
                    width: 35px;
                    height: 35px;
                }
                svg{
                    @media screen and (max-width: 992px){
                        fill: #fff;
                    }
                }          
            }
        }
        .navPages-list--user {
            background: #fff;
            .navPages-action {
                @media only screen and (max-width: 992px){
                    width: unset;
                    justify-content: space-between !important;
                    background: #fff;
                    font-size: 16px;

                }
                &:hover{
                    @media only screen and (max-width: 992px){
                        text-decoration: none !important;
                    }
                }

                
            }
            @media only screen and (max-width: 992px){
                display: flex;
                flex-flow: column nowrap;
                text-align: left;

            }
            .navPages-item{
                border-bottom: 1px solid #cccccc;
            }
            
        }
        .navPages-list--user:first-child {

            .navPages-item{
                display: flex;
                align-items: center;
                border-bottom: 1px solid #cccccc;
            }
            .navPages-action {
                @media only screen and (max-width: 992px){
                    width: unset;
                    padding: 0 16px;
                }
            }
        }

        @media only screen and (max-width: 992px){
            background: rgba(0, 0, 0, 0.5) !important;
            padding-top: 0 !important;
            z-index: 50 !important;
            top: 0px !important;
            position: fixed !important;
            height: 100%;
        }


    }

    .navUser-item--cart {
        @media only screen and (max-width: 992px){
            display: flex;
            flex-flow: row wrap;
            align-items: center;
        }
    }
    .navUser .navUser-item--mobile-search{
        @media only screen and (max-width: 992px){
            display: none !important;
        }
    }
    .navUser .navUser-item--account{
        @media only screen and (max-width: 992px){
            display: none !important;
        }
    }
    header.header .navUser > ul > li:not(:first-child) {
        @media only screen and (max-width: 992px){
            border-left: 0 !important;
        }
    }

    .logo-and-toggle {
        @media only screen and (min-width: 868px) and (max-width: 992px){
            margin-left: 18rem;
        }
        @media only screen and (min-width: 801px) and (max-width: 867px){
            margin-left: 15rem;
        }
    }

/*End of Toggle*/

// Home Header
.home-body{
    header.header {
        background: transparent;
    }
    .navPages-action {
        color: #fff !important;
        font-family: 'Almarai';

        @media screen and (max-width: 992px) {
            color: #222222 !important;
        }
        &:hover{
            text-decoration: 3px solid #fff underline !important;
            text-underline-offset: 0.7em;

            @media screen and (max-width: 992px) {
                text-decoration: 0px solid transparent !important;
                text-underline-offset: unset;
            }
        }
        svg{
            font-size: 1.5rem;
            fill: #fff !important;
            stroke: #fff !important;
        }
    }
    .navUser-action {
        color: #fff !important;
        @media screen and (max-width: 992px) {
            color: #222222 !important;
        }
        .h-show {
            display: none;
        }
        .h-hide {
            display: block;
        }
        &:hover{
            text-decoration: none !important;
        }

    }
    .mobileMenu-toggle .mobileMenu-toggleIcon, .mobileMenu-toggle .mobileMenu-toggleIcon::before, .mobileMenu-toggle .mobileMenu-toggleIcon::after, .mobileMenu-toggle.is-open .mobileMenu-toggleIcon::before, .mobileMenu-toggle.is-open .mobileMenu-toggleIcon::after {
        background: #ffffff !important;
    }
    .h-show {
        display: none !important;
    }
    .h-hide {
        display: block !important;
    }
    .header.slim{
        .h-show {
            display: block !important;
        }
        .h-hide {
            display: none !important;
        }
        .navUser-action {
            color: #222222 !important;
        }
    }

}
//End of Home Header

    header.header {
        background: #fff;
        background-position: top;
        background-repeat: no-repeat;
        background-size: cover !important;
        width: 100%;
        border-bottom: 1px solid #cccccc  !important;
        height: 93px !important;

        @media only screen and (min-width: 801px) and (max-width: 992px){
            height: 58px !important;
        }
        @media screen and (max-width: 800px){
            height: 58px !important;
        }

        .header-logo-image-container {
            height: 50px;
            width: auto;
            @media only screen and (min-width: 801px) and (max-width: 992px){
                min-height: 50px;
            }
            
        }

        .header-logo--wrap {
            background: transparent;
            padding: 0px 0 0px 0;
            height: 100%;
            @media only screen and (max-width: 992px){
                padding: 0; 
            }


            .header-logo{
                justify-content: center;
                max-width: 80%;
                height: 100%;
                @media only screen and (max-width: 992px){
                    padding: 2px 0 0 0;
                    z-index: 10;
                    text-align: center;
                    margin-left: 55px;
                    margin-right: 55px;
                    -ms-flex-pack: center;
                    justify-content: center;
                    height: auto;
                    max-width: 92%;
                }
                @media only screen and (min-width: 801px){
                    justify-content: space-between;
                    padding: 0 0;
                }
                .logo-and-toggle {
                    width: 336px !important;
                }
                h1, div.logo-wrap{
                    @media only screen and (min-width: 993px){
                        flex: 0 1 239px !important;
                    }
                }
            }
            .desktopMenu-toggle {
                @media only screen and (min-width: 801px){
                    display: none;
                }
            }
            
        }
        .quickSearchWrap {
            display: none;
        }
        .mobileMenu-toggle .mobileMenu-toggleIcon, .mobileMenu-toggle .mobileMenu-toggleIcon::before, .mobileMenu-toggle .mobileMenu-toggleIcon::after, .mobileMenu-toggle.is-open .mobileMenu-toggleIcon::before, .mobileMenu-toggle.is-open .mobileMenu-toggleIcon::after {
            background: #222222;
        }
        .mobileMenu-toggle {
            @media only screen and (min-width: 801px) and (max-width: 992px){
                display: inline-block;
                top: 2px;
                left: 0px;
            }
        }
        /*Navigation Menu*/
        .custom-navPages {
            @media screen and (max-width: 992px){
                display: none !important;
            }
            @media only screen and (min-width: 993px) and (max-width: 1160px){
            margin-left: -8rem;
            }
            @media only screen and (min-width: 1161px){
                margin-left: -10rem;
            }
        
            @media only screen and (min-width: 801px){
            display: block;
            background: transparent;
            border: none;
            z-index: 1;
            padding: 0 !important;
            top: 0 !important; 

            .navPages-action {
                color: #222222;
                font-family: 'Almarai';
                font-weight: 600;
                padding: 45px 15px;
                @media only screen and (min-width: 1425px){
                    padding: 45px 35px;
                }

                @media only screen and (min-width: 1000px){
                    letter-spacing: 3px;
                    font-size: 16px;
                }
                @media only screen and (max-width: 1209px){
                    font-size: 14px;
                }
                @media screen and (max-width: 1160px){
                    font-size: 14px !important;
                    letter-spacing: 2px !important;
                }
                &:hover{
                    text-decoration: 3px solid #8bd8bd underline;
                    text-underline-offset: 0.7em;

                    @media screen and (max-width: 992px) {
                        text-decoration: 0px solid transparent !important;
                        text-underline-offset: unset;
                    }
                }
            }
            }

            .navPages-action svg{
                font-size: 1.5rem;
                fill: #222222;
                stroke: #222222;
                width: 2.5rem;
                margin-left: -0.5rem;
                height: 0.7rem;
                margin-top: -0.1rem;
            }

            .navPage-subMenu-action.navPages-action{
                padding: 15px 0;
                font-size: 10px;
                text-transform: uppercase;
                letter-spacing: 0px !important;
                text-align: center;
                font-size: 12px;
                color: #222222 !important;

                &:hover{
                    text-decoration: none !important;
                }

            }
            .navPages-action-moreIcon {
                svg{
                    height: 0.5rem !important;
                    width: 2rem !important;
                }
                @media only screen and (min-width: 801px){
                    display: block;
                    margin-left: -20px !important;
                    margin-top: 0px !important;
                }
            }

        }
        .navPages-action {
            @media screen and (max-width: 812px){
                font-size: 14px ;
            }
        }
        .navPages-container{
            border: none;
        }
    
        .navUser-wrap .navUser ul>li>.icon svg{
            fill: #fff;
        }

        /*Account Sign In*/
        .navUser > ul > li:first-child {
            color: #fff;
        }
        .navUser > ul > li:not(:first-child) {
            @media only screen and (min-width: 801px){
                border-left: 2px solid #D2D2D2;
            }
        }
        
        .navUser-action {
            color: #222222;
            font-weight: 600;
            @media only screen and (max-width: 1209px){
                font-size: 14px !important;
            }
            @media only screen and (max-width: 1160px){
                font-size: 14px !important;
                letter-spacing: 1px;
            }
            @media only screen and (min-width: 1000px){
                letter-spacing: 2px;
                font-size: 16px;
                font-family: 'Almarai';
                text-transform: uppercase;
            }
            @media screen and (max-width: 812px){
                font-size: 12px ;
            }
            &:hover{
                text-decoration: none !important;
            }
        }
        p.cart-quantity {
            background-color: #dd2424;
            position: absolute;
            top: -6px;
            right: -10px;
            border-radius: 20px;
            padding: 0px 4px 6px 6px;
            text-align: center;
            color: white;
            font-size: 12px;
            width: 18px;
            height: 18px;
            line-height: 18px;

            @media screen and (max-width: 800px) {
                top: 14px;
                right: 11px;
                padding: 1px 3px 1px 4px;
            }
        }
        .custom-navPages .navPages-mainNav {
            flex-flow: unset;
        }
        .navUser {
            @media only screen and (min-width: 801px) {
                padding-left: 4px;     
            }

        }
        .h-show{
            display:block;
        }
        .h-hide{
            display: none;
        }
    }
    
// Mobile Navigation Menu
@media screen and (max-width: 992px) {
    img.mt-2.mb-2.nav-img {
        display: none;
    }
    p.categ-name {
        margin-bottom: 0;
        text-transform: capitalize;
        margin-top: 0rem;
        font-size: 16px ;
        color: #222222;
        
    }
    .header.slim .navPages-action:hover {
        text-decoration: none !important;
    }
    .navPage-subMenu.is-open {
        position: relative;
        background: #fff;
        @media screen and (min-width: 801px) and (max-width:992px) {
            padding: 0 1rem 1rem !important;
        }
       
    }
    .navPages li:hover {
        background: #fff !important;
    }

    
}


@media screen and (min-width:801px) {
    .navPages li.navPages-item:hover > .navPages-action:before, .navPage-subMenu .navPages-action:hover:before {
        background: #8bd8bd !important;
    }

    a.navPage-subMenu-action.navPages-action {
        color: #1a2b58 !important;
        text-transform: capitalize;
        letter-spacing: 1px !important;
        text-align: center;
        padding: 15px 0;
    }
    li.navPage-subMenu-item:last-child {
        padding-right: 0!important;
    }
    li.navPage-subMenu-item:first-child {
        padding-left: 0!important;
    }

    .navPages-list:not(.navPages-list-depth-max) .navPages-action.has-subMenu + .navPage-subMenu .navPage-subMenu-list {
        margin-right: auto;
        display: flex;
        width: 77%;
        margin: 0 auto;
        li.navPage-subMenu-item {
            justify-content: center;
            display: grid;
            justify-items: center;
            padding: 0 0.3rem;
            &:hover{
                // text-decoration: 3px solid #8bd8bd underline;
            }
        }
    }
    .navPages-item.hover > .navPage-subMenu {
        display: block !important;
        left: 0 !important;
        top: 93px !important;
        position: fixed;
        transition: 0.5s all 0.5s;
        width: 100%;
        padding: 2.5rem 0 !important;
        background: #f7f7f7;

        @media screen and (max-width: 1200px) {
            padding: 1rem 0 !important;
        }
        @media screen and (max-width: 1080px) {
            padding: 0rem 0 !important;
        }
    }
    .navPages-item.hover:not(:hover) > .navPage-subMenu {
        transition: 5s all 5s
    }

    .nav-img{
        display: block;
        margin: 0 auto;
    }

    .categ-name{
        margin: 1.5rem 0 0 0;
        font-size: 11px;
        padding: 0;
    }
    .dropdown-action{
        padding: 10px 0 !important;
    }
    .h-hide{
        display:none;
    }
}


//========================================================
/*Homepage Carousel */
.home-carousel .heroCarousel-content--wrapper {
    left: 60%;
    .heroCarousel-content {
        text-align: center;
        background-color: transparent;
        top: 60%;
        @media screen and (max-width:1500px) {
            top: 65%;
        }
        @media screen and (max-width:1350px) {
            top: 70%;
        }
        @media screen and (max-width:992px) {
            top: 60%;
        }

        .button:not(.dropdown-button) {
            font-weight: normal;
            font-family: Almarai;
            letter-spacing: 1px;
            padding: 15px 45px;
            font-size: 14px;

            &:hover{
                background: rgb(26, 43, 88);
                color: rgb(139, 216, 189);
                border: 1px solid rgb(26, 43, 88);
            }

            @media screen and (max-width:800px) {
                margin-top: 1rem;
                background: #1a2b58;
                color: #fff;
            }
        }
        p.heroCarousel-description {
            max-width: 65%;
            margin: 0 auto;
            padding: 1rem 0 0.5rem 0;
            font-family: 'Open Sans';
            text-align: center;
            @media screen and (max-width:1200px) {
                font-size: 14px;
            }
            @media screen and (max-width:800px) {
                color: #1a2b58;
            }
            @media screen and (max-width:400px) {
                max-width: 75%;
            }
        }
        .heroCarousel-title {
            font-family: "Almarai" !important;
            font-weight: 400;
            text-transform: capitalize;
            font-size: 36px;
            letter-spacing: 2px;
            @media screen and (max-width:1200px) {
                font-size: 28px;
            }

            @media screen and (max-width:800px) {
                color: #1a2b58;
            }
            
            
        }

    }
}
//========================================================
/*Homepage Featured*/
.homepage-featured{
    max-width: 80%;
    margin: 3.5rem auto 0;
    .page-heading{
        color: #222222;
        font-weight: normal;
        font-family: 'Dubai', sans-serif;
        letter-spacing: 2px;
        margin: 0 0 2.5rem;
    }
    ul>li.product:last-child {
        padding-right: 0 !important;
    }
    ul>li.product:first-child {
        padding-left: 0;
    }
    ul>li.product{
        @media screen and (max-width:480px) {
            padding-left: 0 !important;
        }
    }
    .productGrid{
        width: 100%;
        margin: 0;

        .card .card-body{
            justify-content: center;
            border-right: 0px solid #ebebeb;
            border-left: 0px solid #ebebeb;
            padding: 0 0 0;

            & > :first-child{
                margin-bottom: 1rem;
            }

        }
        .card .card-title {
            text-align: center;
            max-width: 80%;
            margin: 0 auto;
            a{
                color: #444444;
                font-weight: 500;
                font-family: 'Almarai' !important;
            }  
        }
        .card .card-text.brand-name{
            padding: 1.5rem 0 0.2rem 0;
            font-family: 'Almarai';
            color: #666666;
            text-transform: uppercase;
            font-size: 14px;
            margin-top: 0.5rem;
            margin-bottom: 0.4rem;
        }
        .card .card-text{
            justify-content: center;
            text-align: center;
            padding: 0.5rem 0 0rem 0 !important;
            font-family: 'Almarai';
            margin-bottom: 0;
            
            .price-section{
                justify-content: center;
                
            }
            span.price.price--withoutTax {
                font-weight: 800;
            }
        }
        .card-text.add-to-cart-button{
            display: block; 
        }
        .card .button {
            padding: 15px 15px;
            background: #fff;
            border: 1px solid #8bd8bd;
            color: #1a2b58;
            margin-bottom: 0;
            font-family: 'Almarai';
            letter-spacing: 2px;
            border-radius: 0;

            &:hover{
                background: #1a2b58;
                color: #8bd8bd;
                border: 1px solid #1a2b58;
                text-decoration: none;
            }

        }
        .card .card-figure{
            border-bottom: 0;
            border: none;
        }
        .card{
            padding: 1rem;
        }

    }
    .top-seller {
        position: relative;
        padding-top: 1.5rem;
        a.best-seller-link {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: #1a2b58;
            color: #fff;
            padding: 15px 15px;
            text-transform: uppercase;
            font-family: "Almarai";
            letter-spacing: 2px;
            font-weight: 400;
            width: 25%;
            text-align: center;
            @media screen and(min-width:481px) and (max-width: 992px) {
                font-size: 14px;
                letter-spacing: 0px;
            }
            @media screen and (max-width: 800px) {
                width: 50%;
            }
            @media screen and (max-width: 480px) {
                width: 100%;
            }
            &:hover{
                background: #1a2b58;
                color: #8bd8bd;
                border: 1px solid #1a2b58;
                text-decoration: none;
            }
            @media screen and (max-width: 637px) {
                padding: 15px 25px;
            }
            @media screen and (max-width: 480px) {
                padding: 15px 15px;
                font-size: 10px !important;
            }
            @media screen and (max-width: 400px) {
                font-size: 10px !important;
            }

        }
    }

    .productGrid .product {
        @media screen and (max-width: 800px) {
            flex: 0 1 50% !important;
            max-width: 50% !important;
            padding-left: 0.75rem;
            padding-right: 0;
        }
        @media screen and (max-width: 480px) {
            flex: 0 1 100% !important;
            max-width: 100% !important;
        }
    }
    
}

//========================================================
/*Shop By Need*/
.Shop-by-need {
    margin: 3.5rem auto 0;
    .content-title {
        text-align: center;
        border-top: 1px solid #cccccc;
        width: 85%;
        margin: 0 auto;
        h2{
            color: #222222;
            font-family: "Almarai", sans-serif !important;
            font-weight: 400;
            letter-spacing: 2px;
            margin: 3.5rem 0;
        }
    }
}
.shop-by-need-images{
    margin: 2.5rem auto 0.5rem;
}
.flex-four-img {

    @media only screen and (min-width: 481px){
        display: flex;
        flex-flow: row wrap;
        justify-content: space-between;
    }
}
.flex-four-img>div {
    width: 49.4%;
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    padding: 0.7rem 0;
    @media screen and (max-width: 736px){
        width: 85%;
        margin: 0 auto;
    }
}
.flex-four-img>div>div, .flex-four-img>div>img {
    grid-row: 1 / 2;
    align-self: center;
}

.flex-four-img>div>img {
    grid-column: 1 / 5;
    display: block;
    max-width: 100%;
    align-self: auto;
    width: 100%;
    
}

.flex-four-img>div>div {
    grid-column: 1 / 5;
    text-align: left;
    padding-left: 4rem;
    @media screen and (max-width: 400px){
        padding-left: 3rem;
    }
    h2{
        margin: 0 0 0.5rem;
        color: #fff;
